// Obtener configuraci√≥n del nodo anterior
const config = $input.first().json;
const hosts = config.hosts;
const pathsToCheck = config.paths;
const scanTimeout = config.scanTimeout || 10000;
const delayBetweenRequests = config.delayBetweenRequests || 100;

console.log(`Iniciando escaneo de ${hosts.length} hosts...`);

// Funci√≥n para determinar severidad
function getSeverity(path, httpCode) {
  if (httpCode !== 200) return 'BAJA';

  const pathLower = path.toLowerCase();

  if (pathLower.includes('.env') || pathLower.includes('.git') || 
      pathLower.includes('database') || pathLower === 'config') {
    return 'CR√çTICA';
  }

  if (pathLower.includes('storage') && !pathLower.includes('public/storage')) {
    return 'ALTA';
  }

  if (pathLower.includes('vendor') || pathLower.includes('composer')) {
    return 'MEDIA';
  }

  return 'BAJA';
}

// Funci√≥n para verificar URL con timeout
async function checkUrl(url, timeout) {
  const controller = new AbortController();
  const timeoutId = setTimeout(() => controller.abort(), timeout);

  try {
    const response = await fetch(url, {
      method: 'HEAD',
      redirect: 'manual',
      signal: controller.signal,
      headers: {
        'User-Agent': 'Security-Audit-Bot/n8n/2.0'
      }
    });

    clearTimeout(timeoutId);

    return {
      status: response.status,
      size: response.headers.get('content-length') || '0',
      contentType: response.headers.get('content-type') || 'unknown',
      success: true
    };
  } catch (error) {
    clearTimeout(timeoutId);

    return {
      status: 0,
      size: '0',
      contentType: 'error',
      error: error.name === 'AbortError' ? 'Timeout' : error.message,
      success: false
    };
  }
}

// Escanear todos los hosts
const results = [];
let totalCritical = 0;
let totalWarnings = 0;
let totalSecure = 0;
let totalScanned = 0;

for (const host of hosts) {
  console.log(`[${++totalScanned}/${hosts.length}] Escaneando: ${host}`);

  const hostResult = {
    host: host,
    findings: [],
    critical: 0,
    warnings: 0,
    low: 0,
    isSecure: true,
    scannedPaths: 0,
    errorPaths: 0
  };

  for (const path of pathsToCheck) {
    const url = `https://${host}/${path}`;

    const result = await checkUrl(url, scanTimeout);
    hostResult.scannedPaths++;

    if (!result.success) {
      hostResult.errorPaths++;
      continue;
    }

    if (result.status === 200) {
      const severity = getSeverity(path, result.status);

      hostResult.findings.push({
        url: url,
        path: path,
        status: result.status,
        size: result.size,
        contentType: result.contentType,
        severity: severity,
        timestamp: new Date().toISOString()
      });

      hostResult.isSecure = false;

      if (severity === 'CR√çTICA') {
        hostResult.critical++;
        totalCritical++;
      } else if (severity === 'ALTA') {
        hostResult.warnings++;
        totalWarnings++;
      } else if (severity === 'MEDIA') {
        hostResult.warnings++;
        totalWarnings++;
      } else {
        hostResult.low++;
      }
    }

    // Pausa entre requests
    await new Promise(resolve => setTimeout(resolve, delayBetweenRequests));
  }

  if (hostResult.isSecure) {
    totalSecure++;
  }

  results.push(hostResult);
}

// Calcular m√©tricas adicionales
const vulnerableHosts = results.filter(h => !h.isSecure);
const criticalHosts = results.filter(h => h.critical > 0);
const totalFindings = results.reduce((sum, h) => sum + h.findings.length, 0);

// Preparar output
const output = {
  timestamp: new Date().toISOString(),
  scanDuration: 'completed',
  summary: {
    totalHosts: hosts.length,
    secureHosts: totalSecure,
    vulnerableHosts: vulnerableHosts.length,
    criticalHosts: criticalHosts.length,
    criticalVulnerabilities: totalCritical,
    warnings: totalWarnings,
    totalFindings: totalFindings,
    pathsScanned: pathsToCheck.length,
    totalRequests: hosts.length * pathsToCheck.length
  },
  topVulnerable: vulnerableHosts
    .sort((a, b) => b.critical - a.critical || b.warnings - a.warnings)
    .slice(0, 5)
    .map(h => ({
      host: h.host,
      critical: h.critical,
      warnings: h.warnings,
      totalFindings: h.findings.length
    })),
  hosts: results,
  config: {
    emailFrom: config.emailFrom,
    emailTo: config.emailTo,
    slackChannel: config.slackChannel
  }
};

console.log(`Escaneo completado: ${totalFindings} hallazgos en ${vulnerableHosts.length} hosts`);

return output;

